//Function:transfer dataframe to Iterable
def sql2seq (seq: Array[org.apache.spark.sql.Row]) : List[(String,String)] = { 
    var test : List[(String,String)] = List()
    var c = ""
    for ( i <- seq ){
        c = i.toString.replace("[","").replace("]","")
        test = test.+:(c,c)
    }
    return test
}

